import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; 
import { useHistory } from 'react-router-dom';
import { QuestionsProvider, useQuestions } from './QuestionsContext';

const Welcome = () => {
    const [name, setName] = useState('');
    const [loading, setLoading] = useState(false);
    const { storeQuestions } = useQuestions(); // Using the new helper function
    const history = useHistory(); // Initialize useHistory hook

    const handleInputChange = (event) => {
        setName(event.target.value);
    };

    const fetchQuestions = async () => {
        setLoading(true); // Show loading animation
        try {
            const response = await fetch('https://6qpujfk4qe.execute-api.us-west-1.amazonaws.com/prod/get-questions', {
                method: 'GET',
                headers: { 'Content-Type': 'application/json' },
            });
            const data = await response.json();
            if (response.ok) {
                storeQuestions(data.questions); // Store fetched questions
                history.push('/questions'); // Redirect to questions page
            } else {
                throw new Error('Failed to load questions');
            }
        } catch (error) {
            console.error('Failed to fetch questions:', error);
        } finally {
            setLoading(false); // Hide loading animation
        }
    };

    const styleSettings = {
        title: {
            fontSize: '3em',
            color: 'white',
            textAlign: 'center',
            marginBottom: '0.5em'
        },
        inputStyle: {
            maxWidth: '300px',
            margin: 'auto',
            borderRadius: '1',
            height: '38px',
        },
        buttonStyle: {
            maxWidth: '300px',
            margin: 'auto',
            borderRadius: '1', 
            height: '38px',
            width: '80%',
            lineHeight: '38px',
            padding: '0',
        }
    };

    if (loading) {
        return <div className="loading-container">Loading...</div>; // Simple loading text, customize as needed
    }

    return (
        <div className="container text-center mt-5">
            <h1 style={styleSettings.title}>WAVELENGTH</h1>
            <div className="form-group mt-4">
                <input 
                    type="text" 
                    className="form-control"
                    placeholder="Your Name" 
                    value={name} 
                    onChange={handleInputChange}
                    style={styleSettings.inputStyle}
                />
            </div>
            <div className="d-grid gap-2 mt-3" style={{ maxWidth: '300px', margin: 'auto' }}>
                <button className="btn btn-secondary btn-lg" type="button" onClick={fetchQuestions} style={styleSettings.buttonStyle} disabled={loading}>
                    {loading ? 'Loading...' : 'Play'}
                </button>
                <button className="btn btn-secondary btn-lg" type="button" style={styleSettings.buttonStyle}>Join Using Code</button>
            </div>
        </div>
    );
};

export default Welcome;
