import React, { useState, useEffect } from 'react';
import { useQuestions } from './QuestionsContext';

const DisplayQuestions = () => {
    const { questions } = useQuestions();
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [responses, setResponses] = useState(new Array(questions.length).fill(0));
    const [colors, setColors] = useState([]);
    const [selectedOptionIndex, setSelectedOptionIndex] = useState(-1);

    // Define color options for the glow effect
    const colorOptions = [
        "#8A2BE2", // Violet
        "#4B0082", // Indigo
        "#0000FF", // Blue
        "#008000", // Green
        "#FFFF00", // Yellow
        "#FFA500", // Orange
        "#FF0000"  // Red
    ];

    useEffect(() => {
        setColors(shuffle([...colorOptions]));
    }, [currentQuestionIndex]);

    const handleOptionClick = (optionIndex) => {
        setSelectedOptionIndex(optionIndex);
        const updatedResponses = [...responses];
        updatedResponses[currentQuestionIndex] = optionIndex + 1;
        setResponses(updatedResponses);

        if (currentQuestionIndex < questions.length - 1) {
            setTimeout(() => {
                setCurrentQuestionIndex(currentQuestionIndex + 1);
                setSelectedOptionIndex(-1); // Reset selection
            }, 1000); // Delay to show selection effect
        } else {
            console.log('End of questions', updatedResponses);
        }
    };

    if (questions.length === 0) {
        return <div>Loading questions...</div>;
    }

    const { question, options } = questions[currentQuestionIndex];

    return (
        <div className="container text-center mt-5">
            <h2>{question}</h2>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px' }}>
                {options.map((option, index) => (
                    <button
                        key={index}
                        style={{
                            backgroundColor: 'black', // Eggshell color
                            color: 'black',
                            padding: '10px 20px',
                            border: 'none',
                            borderRadius: '5px',
                            boxShadow: selectedOptionIndex === index ? `0 0 8px 3px ${colors[index]}` : 'none', // Glow effect for selected button
                        }}
                        onClick={() => handleOptionClick(index)}
                    >
                        {option}
                    </button>
                ))}
            </div>
        </div>
    );
};

function shuffle(array) {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]]; // Swap elements
    }
    return array;
}

export default DisplayQuestions;
