import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
// Optional: import axios if you prefer using axios
// import axios from 'axios';

const Welcome = () => {
    const [name, setName] = useState(''); // State to hold the name input
    const [loading, setLoading] = useState(false); // State to handle loading status

    const handleInputChange = (event) => {
        setName(event.target.value);
    };

    const fetchQuestions = async () => {
        setLoading(true); // Set loading to true while fetching
        try {
            const response = await fetch('https://your-api-endpoint.com/get-questions', {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            const data = await response.json(); 
            console.log(data); 
        } catch (error) {
            console.error('Failed to fetch questions:', error);
        } finally {
            setLoading(false);
        }
    };

    const styleSettings = {
        title: {
            fontSize: '3em',
            color: 'white',
            textAlign: 'center',
            marginBottom: '0.5em'
        },
        inputStyle: {
            maxWidth: '300px',
            margin: 'auto',
            borderRadius: '2',
            height: '38px',
        },
        buttonStyle: {
            maxWidth: '300px',
            margin: 'auto',
            borderRadius: '0', // Sharp corners
            height: '38px', // Match input height
            width: '80%', // Reduce width
            lineHeight: '38px', // Set line height to match button height
            padding: '0', // Remove padding to prevent shifting
        }
    };

    return (
        <div className="container text-center mt-5">
            <h1 style={styleSettings.title}>WAVELENGTH</h1>
            <div className="form-group mt-4">
                <input 
                    type="text" 
                    className="form-control"
                    placeholder="Your Name" 
                    value={name} 
                    onChange={handleInputChange}
                    style={styleSettings.inputStyle}
                />
            </div>
            <div className="d-grid gap-2 mt-3" style={{ maxWidth: '300px', margin: 'auto' }}>
                <button className="btn btn-secondary btn-lg" type="button" onClick={fetchQuestions} style={styleSettings.buttonStyle} disabled={loading}>
                    {loading ? 'Loading...' : 'Play'}
                </button>
                <button className="btn btn-secondary btn-lg" type="button" style={styleSettings.buttonStyle}>Join Using Code</button>
            </div>
        </div>
    );
};

export default Welcome;
