import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

const Welcome = () => {
    const [name, setName] = useState(''); // State to hold the name input

    const handleInputChange = (event) => {
        setName(event.target.value);
    };

    // Inline styles for elements
    const styleSettings = {
        container: {
            display: 'flex', // Enable Flexbox
            flexDirection: 'column', // Stack children vertically
            justifyContent: 'center', // Center vertically
            alignItems: 'center', // Center horizontally
            height: '100vh', // Full viewport height
            textAlign: 'center'
        },
        title: {
            fontSize: '3em', // Increase size
            color: 'white',
            marginBottom: '0.5em'
        },
        inputStyle: {
            maxWidth: '300px',
            borderRadius: '0', // Sharp corners
            height: '38px', // Explicit height
        },
        buttonStyle: {
            maxWidth: '300px',
            borderRadius: '0', // Sharp corners
            height: '38px', // Match input height
            width: '80%', // Reduce width
            lineHeight: '38px', // Set line height to match button height
            padding: '0', // Remove padding to prevent shifting
        }
    };

    return (
        <div style={styleSettings.container}>
            <h1 style={styleSettings.title}>WAVELENGTH</h1>
            <div className="form-group mt-4">
                <input 
                    type="text" 
                    className="form-control"
                    placeholder="Your Name" 
                    value={name} 
                    onChange={handleInputChange}
                    style={styleSettings.inputStyle}
                />
            </div>
            <div className="d-grid gap-2 mt-3">
                <button className="btn btn-primary btn-lg" type="button" style={styleSettings.buttonStyle}>Play</button>
                <button className="btn btn-secondary btn-lg" type="button" style={styleSettings.buttonStyle}>Join Using Code</button>
            </div>
        </div>
    );
};

export default Welcome;
