import React, { useState } from 'react';
import { QuestionsProvider, useQuestions } from './QuestionsContext';
import 'bootstrap/dist/css/bootstrap.min.css';

const Welcome = () => {
    const [name, setName] = useState('');
    const [loading, setLoading] = useState(false);
    const { storeQuestions } = useQuestions(); // Using the new helper function

    const handleInputChange = (event) => {
        setName(event.target.value);
    };

    const fetchQuestions = async () => {
        setLoading(true);
        try {
            const response = await fetch('https://6qpujfk4qe.execute-api.us-west-1.amazonaws.com/prod/get-questions', {
                method: 'GET',
                headers: { 'Content-Type': 'application/json' },
            });
            const data = await response.json();
            if (response.ok) {
                storeQuestions(data.questions); // Process and store fetched questions using the helper function
            } else {
                throw new Error('Failed to load questions');
            }
        } catch (error) {
            console.error('Failed to fetch questions:', error);
        } finally {
            setLoading(false);
        }
    };

    // Styles omitted for brevity

    return (
        <div className="container text-center mt-5">
            <h1 style={styleSettings.title}>WAVELENGTH</h1>
            // Other UI components
        </div>
    );
};

const WelcomeWithProvider = () => (
    <QuestionsProvider>
        <Welcome />
    </QuestionsProvider>
);

export default WelcomeWithProvider;
