import React from 'react';
import './DonutProgressBar.css';

const DonutProgressBar = () => {
    let percentage = 40;
    const getGradient = (percentage) => {
        const colorStops = [
            { color: '#ea2f86', stop: 10 },
            { color: '#f09c0a', stop: 20 },
            { color: '#fae000', stop: 30 },
            { color: '#93e223', stop: 40 },
            { color: '#4070d3', stop: 50 },
            { color: '#493c9e', stop: 60 },
            { color: '#ea2f86', stop: 70 },
            { color: '#f09c0a', stop: 80 },
            { color: '#fae000', stop: 90 },
            { color: '#93e223', stop: 100 }
        ];

        let gradient = colorStops.reduce((acc, stop) => {
            if (percentage >= stop.stop) {
                acc += `${stop.color} ${stop.stop}%, `;
            } else if (acc === '') { // This covers the case where the percentage is less than the first stop
                acc += `${stop.color} ${percentage}%, black ${percentage}%, black 100%`;
                return acc;
            }
            return acc;
        }, '');

        // Close the gradient if no black was added (i.e., percentage was >= last stop)
        if (!gradient.includes('black')) {
            gradient += `black ${percentage}%, black 100%`;
        }

        return `conic-gradient(${gradient})`;
    };

    return (
        <div className="circle-one" style={{ background: getGradient(percentage) }}>
            <div className="inner-circle"></div>
            <div className="percentage">
                {percentage/10}
            </div>
        </div>
    );
}

export default DonutProgressBar;
