import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const Welcome = () => {
    const [name, setName] = useState('');

    const handleInputChange = (event) => {
        setName(event.target.value);
    };

    const containerStyle = {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        width: '100vw', // This ensures the container takes full width of the viewport
        textAlign: 'center',
        backgroundColor: 'black', // Optional: Ensure background is consistent
    };

    const titleStyle = {
        fontSize: '3em',
        marginBottom: '0.5em'
    };

    const inputStyle = {
        maxWidth: '300px',
        borderRadius: '0',
        height: '38px',
    };

    const buttonStyle = {
        maxWidth: '300px',
        borderRadius: '0',
        height: '38px',
        width: '80%',
        lineHeight: '38px',
        padding: '0',
    };

    return (
        <div style={containerStyle}>
            <h1 style={titleStyle}>WAVELENGTH</h1>
            <div className="form-group mt-4">
                <input 
                    type="text" 
                    className="form-control"
                    placeholder="Your Name" 
                    value={name} 
                    onChange={handleInputChange}
                    style={inputStyle}
                />
            </div>
            <div className="d-grid gap-2 mt-3">
                <button className="btn btn-primary btn-lg" type="button" style={buttonStyle}>Play</button>
                <button className="btn btn-secondary btn-lg" type="button" style={buttonStyle}>Join Using Code</button>
            </div>
        </div>
    );
};

export default Welcome;
