import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

const Welcome = () => {
    const [name, setName] = useState(''); // State to hold the name input

    const handleInputChange = (event) => {
        setName(event.target.value);
    };

    const styleSettings = {
        title: {
            fontSize: '3em', // Increase size
            color: 'white',
            textAlign: 'center',
            marginBottom: '0.5em'
        },
        inputStyle: {
            maxWidth: '300px',
            margin: 'auto',
            borderRadius: '0', // Sharp corners
            height: '38px', // Explicit height
        },
        buttonStyle: {
            maxWidth: '300px',
            margin: 'auto',
            borderRadius: '0', // Sharp corners
            height: '38px', // Match input height
            width: '80%', // Reduce width
            lineHeight: '38px', // Set line height to match button height
            padding: '0', // Remove padding to prevent shifting
        },
        lineStyle: {
            width: '2px', // Width of the line
            height: '90vh', // Line extends 90vh upwards from the bottom
            backgroundColor: 'white', // Line color
            position: 'absolute', // Needed to position correctly
            bottom: '0', // Start from the bottom
            left: '50%', // Center the line horizontally
            transform: 'translateX(-50%)' // Ensure it's perfectly centered
        }
    };

    return (
        <div className="container text-center mt-5" style={{ position: 'relative' }}>
            <h1 style={styleSettings.title}>WAVELENGTH</h1>
            <div className="form-group mt-4">
                <input 
                    type="text" 
                    className="form-control"
                    placeholder="Your Name" 
                    value={name} 
                    onChange={handleInputChange}
                    style={styleSettings.inputStyle}
                />
            </div>
            <div className="d-grid gap-2 mt-3" style={{ maxWidth: '300px', margin: 'auto' }}>
                <button className="btn btn-secondary btn-lg" type="button" style={styleSettings.buttonStyle}>Play</button>
                <button className="btn btn-secondary btn-lg" type="button" style={styleSettings.buttonStyle}>Join Using Code</button>
            </div>
        </div>
    );
};

export default Welcome;
