import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

const Welcome = () => {
    const [name, setName] = useState(''); // State to hold the name input

    const handleInputChange = (event) => {
        setName(event.target.value);
    };

    return (
        <div className="container text-center mt-5">
            <h1 className="mb-4" style={{ color: 'white', fontSize: '2.5em' }}>WAVELENGTH</h1>
            <div className="form-group mt-4">
                <input 
                    type="text" 
                    className="form-control" 
                    placeholder="Your Name" 
                    value={name} 
                    onChange={handleInputChange}
                    style={{ maxWidth: '300px', margin: 'auto' }}
                />
            </div>
            <div className="d-grid gap-2 mt-3" style={{ maxWidth: '300px', margin: 'auto' }}>
                <button className="btn btn-secondary btn-lg" type="button">Play</button>
                <button className="btn btn-secondary btn-lg" type="button">Join Using Code</button>
            </div>
        </div>
    );
};

export default Welcome;
