import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

const Welcome = () => {
    const [name, setName] = useState(''); // State to hold the name input

    const handleInputChange = (event) => {
        setName(event.target.value);
    };

    return (
        <div className="container mt-5">
            <h1 className="text-center">Wavelength</h1>
            <div className="form-group">
                <input 
                    type="text" 
                    className="form-control" 
                    placeholder="Enter your name" 
                    value={name} 
                    onChange={handleInputChange}
                />
            </div>
            <div className="d-grid gap-2 mt-3">
                <button className="btn btn-primary" type="button">Play</button>
                <button className="btn btn-secondary" type="button">Join</button>
            </div>
        </div>
    );
};

export default Welcome;
