import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const Scoreboard = () => {
    const { gameId } = useParams();
    const [players, setPlayers] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchScores = async () => {
            setIsLoading(true);
            try {
                const response = await fetch(`https://6qpujfk4qe.execute-api.us-west-1.amazonaws.com/prod/get-all-players?gameId=${gameId}`);
                const data = await response.json();
                if (response.ok) {
                    setPlayers(data.players);
                } else {
                    throw new Error(data.message || "Failed to fetch scores");
                }
            } catch (err) {
                setError(err.message);
                console.error("Fetching scores failed:", err);
            } finally {
                setIsLoading(false);
            }
        };

        fetchScores();
    }, [gameId]);

    if (isLoading) return <div>Loading scores...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div className="scoreboard">
            <h2>Scoreboard for Game ID: {gameId}</h2>
            {players.length > 0 ? (
                <ul>
                    {players.map(player => (
                        <li key={player.playerID}>
                            {player.playerName} - Score: {player.score}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No scores available.</p>
            )}
        </div>
    );
};

export default Scoreboard;
