import React, { useState, useEffect } from 'react';
import { useQuestions } from './QuestionsContext';

const ClockTimer = ({ onTimerEnd }) => {
    useEffect(() => {
        const timer = setTimeout(onTimerEnd, 7000); // 7 seconds for full rotation
        return () => clearTimeout(timer);
    }, [onTimerEnd]);

    return (
        <div className="clock">
            <div className="hand"></div>
        </div>
    );
}

const DisplayQuestions = () => {
    const { questions } = useQuestions();
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [responses, setResponses] = useState(new Array(questions.length).fill(0));
    const [selectedOptionIndex, setSelectedOptionIndex] = useState(-1);

    const colors = ['#FF6347', '#4682B4', '#32CD32', '#FFD700'];

    const handleOptionClick = (optionIndex) => {
        setSelectedOptionIndex(optionIndex);
        const updatedResponses = [...responses];
        updatedResponses[currentQuestionIndex] = optionIndex + 1;
        setResponses(updatedResponses);

        moveToNextQuestion();
    };

    const moveToNextQuestion = () => {
        if (currentQuestionIndex < questions.length - 1) {
            setCurrentQuestionIndex(currentQuestionIndex + 1);
            setSelectedOptionIndex(-1); // Reset selection
        } else {
            console.log('End of questions', updatedResponses);
        }
    };

    if (questions.length === 0) {
        return <div>Loading questions...</div>;
    }

    const { question, options } = questions[currentQuestionIndex];
    const roundText = `Wavelength round ${currentQuestionIndex + 1} of ${questions.length}`;

    return (
        <div className="container text-center mt-5">
            <h2>{roundText}</h2>
            <ClockTimer onTimerEnd={moveToNextQuestion} />
            <h4>{question}</h4>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px' }}>
                {options.map((option, index) => (
                    <button
                        key={index}
                        style={{
                            backgroundColor: colors[index],
                            color: 'white',
                            padding: '10px 20px',
                            border: '2px solid gray',
                            borderRadius: '5px',
                            boxShadow: selectedOptionIndex === index ? `0 0 8px 3px ${colors[index]}` : 'none',
                        }}
                        onClick={() => handleOptionClick(index)}
                    >
                        {option}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default DisplayQuestions;
