import React, { useState, useContext } from 'react';
import { QuestionsContext } from './QuestionsContext';

const DisplayQuestions = () => {
    const { questions, currentQuestionIndex } = useContext(QuestionsContext);
    const [selectedOption, setSelectedOption] = useState(null);

    if (!questions.length || questions.length <= currentQuestionIndex) {
        return <div>Loading questions...</div>;
    }

    const currentQuestion = questions[currentQuestionIndex];

    const handleOptionClick = (index) => {
        setSelectedOption(index);
        // Add logic to move to the next question or handle the end of the quiz
    };

    return (
        <div className="question-container" style={{ padding: '20px', maxWidth: '600px', margin: 'auto' }}>
            <h3 style={{ color: 'white', textAlign: 'center' }}>{currentQuestion.question}</h3>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                {currentQuestion.options.map((option, index) => (
                    <button
                        key={index}
                        onClick={() => handleOptionClick(index)}
                        style={{
                            background: selectedOption === index ? 'none' : 'black',
                            color: 'white',
                            border: '2px solid gray',
                            padding: '10px 20px',
                            cursor: 'pointer',
                            outline: selectedOption === index ? '3px solid orange' : 'none',
                            boxShadow: '0px 0px 8px rgba(255, 165, 0, 0.8)'
                        }}
                    >
                        {option}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default DisplayQuestions;
